mapper.select:
select item_num, item_name, item_type, item_price, item_state, item_note from item


	item_type = #{item_type}
		 and item_name like concat('%', #{item_name}, '%')

		 and item_price = #{item_price}



		 item_name like concat('%', #{item_name}, '%')

		 and item_price = #{item_price}



	order by item_num desc


select o.od_num, s.name, i.item_name, o.od_amount, o.od_time, o.received_not  
from `order` o join store s on o.num = s.num 
join item i on o.item_num = i.item_num;
